let options={},optionEls=document.querySelectorAll("input");chrome.storage.sync.get("options",n=>{if(console.log(n.options),n.options){for(o in n.options)document.querySelector(`#${o}`).checked=Boolean(n.options[o]);Object.assign(options,n.options)}});for(let o=0;o<optionEls.length;o++)optionEls[o].addEventListener("change",n=>{options[optionEls[o].id]=Boolean(optionEls[o].checked),chrome.storage.sync.set({options:options}),console.log(options)});